import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";

function SectionCourses() {
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [coursesPerPage] = useState(6);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_URL}/get-courses`)
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setCourses(data.courses);
        } else {
          setError("Failed to fetch courses.");
        }
      })
      .catch(() => setError("Server error. Please try again later."));
  }, []);

  function getDateDifference(startDate) {
    const now = new Date();
    const start = new Date(startDate);
    if (isNaN(start)) return { diffDays: 0, diffMonths: 0 };
    const diffTime = now - start;
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    const diffMonths = Math.floor(diffTime / (1000 * 60 * 60 * 24 * 30));
    return { diffDays, diffMonths };
  }

  const indexOfLastCourse = currentPage * coursesPerPage;
  const indexOfFirstCourse = indexOfLastCourse - coursesPerPage;
  const currentCourses = courses.slice(indexOfFirstCourse, indexOfLastCourse);

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <h2 className="text-3xl font-semibold text-center text-gray-800 mb-6">
        Explore Our Courses
      </h2>
      {error ? (
        <p className="text-red-500 text-center">{error}</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {currentCourses.length > 0 ? (
            currentCourses.map((course) => {
              const { diffDays, diffMonths } = getDateDifference(course.createdAt);
              return (
                <div
                  key={course._id}
                  className="bg-white shadow-lg rounded-lg overflow-hidden hover:shadow-xl transition-all duration-300"
                >
                  <img
                    src={`${process.env.REACT_APP_API_URL}/${course.courseimage}`}
                    alt={course.coursetitle}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-4">
                    <h3 className="text-xl font-semibold text-gray-800 flex items-center">
                      <i className="fa fa-graduation-cap text-blue-500 mr-2"></i>
                      {course.coursetitle}
                    </h3>
                    <p className="text-gray-600 text-sm mt-1 flex items-center">
                      <i className="fa fa-clock text-blue-500 mr-2"></i>
                      {course.courseduration}
                    </p>
                    <p className="text-gray-600 text-sm mt-1 flex items-center">
                      <i className="fa fa-book text-blue-500 mr-2"></i>
                      {course.coursedescription ? course.coursedescription : "No description available"}
                    </p>
                    <p className="text-gray-600 text-sm mt-1 flex items-center">
                      <i className="fa fa-chair text-blue-500 mr-2"></i>
                      {course.courseseat ? course.courseseat : "Seats not specified"} Seats
                    </p>
                    <p className="text-gray-600 text-sm mt-1 flex items-center">
                      <i className="fa fa-dollar-sign text-blue-500 mr-2"></i>
                      {course.placementprice ? course.placementprice : "Price not available"}
                    </p>
                    <div className="mt-3 flex items-center justify-between">
                      <span className="text-xl font-bold text-blue-600">
                        ₹{new Intl.NumberFormat('en-IN', { maximumFractionDigits: 0 }).format(course.courseprice)}
                      </span>
                      <span className="text-sm text-gray-500">(15% off)</span>
                    </div>
                    <NavLink
                      to="/contact-us"
                      className="block mt-4 bg-blue-500 text-white text-center py-2 rounded-md font-medium hover:bg-blue-600 transition"
                    >
                      Buy Now
                    </NavLink>
                  </div>
                </div>
              );
            })
          ) : (
            <p className="text-center text-gray-600">No courses available.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default SectionCourses;
